import { useState } from "react";
import styles from "../styles/Products.module.css";
import ProductGrid from "../js/ProductGrid";

function Products() {
  const [option, setOption] = useState("inStock");

  return (
    <div>
      <h1 className={styles.title}> Products</h1>
      <div className={styles.buttonContainer}>
        <div className={styles.switcher}>
          <button style={{ textAlign: "right" }} onclick={setOption("inStock")}>
            In stock
          </button>
          <p>|</p>
          <button style={{ textAlign: "left" }} onclick={setOption("order")}>
            Order available
          </button>
        </div>
      </div>
      <ProductGrid option={option} />
    </div>
  );
}

export default Products;
